sys_variable_value.value:
//requested apps list
var rstr = current.applications.toString();
var rapps = rstr.split(",");

//check user existing active apps for user
var uapps = [];
var eapps = new GlideRecord('x_shce_systems_acc_user_system_access');
eapps.addQuery('user', current.requested_for);
eapps.addNullQuery('deprovision_date');
eapps.query();
while (eapps.next()) {
	uapps.push(eapps.application.sys_id.toString());
}


//compare requested apps with existing apps to create Add tasks
for (x=0; x<=rapps.length; x++) {
	//requested app not found for user existing apps
	if (uapps.indexOf(rapps[x]) == -1) {
		gs.info(rapps[x] + ' ' + uapps.indexOf(rapps[x]));
		//Create Add access Task
		var gr = new GlideRecord("cmdb_ci_appl");
		if (gr.get(rapps[x])) {
			var sr = new GlideRecord("x_shce_systems_acc_sac_task");
			sr.initialize();
			sr.short_description = "Systems Access Request - Add Access " + gr.name + ' ' + gr.u_environment;
			sr.assignment_group = gr.support_group;
			sr.sac_request = current.sys_id;
			sr.parent = current.sys_id;
			sr.approval = 'approved';
			sr.cmdb_ci = gr.sys_id;
			
			if (gr.name =='Soarian Clinicals' ||gr.name =='3M HDM with Worklists' || gr.name =='3M HDM without Worklists' ||gr.name =='Soarian Financials' ||gr.name =='Soarian Document Management' ){

sr.state = -5; 
sr.insert();

}
			else
			sr.insert();
		}
	}
}

//compare existing apps with requested apps to create Remove Tasks
for (y=0; y<=uapps.length; y++) {
	//existing app not found in requested apps list
	if (rapps.indexOf(uapps[y]) == -1) {
		var ar = new GlideRecord("cmdb_ci_appl");
		if (ar.get(uapps[y])) {
			var br = new GlideRecord("x_shce_systems_acc_sac_task");
			br.initialize();
			br.short_description = "Systems Access Request - Remove Access " + ar.name + ' ' + ar.u_environment;
			br.assignment_group = ar.support_group;
			br.sac_request = current.sys_id;
			br.parent = current.sys_id;
			br.approval = 'approved';
			br.cmdb_ci = ar.sys_id;
			br.insert();
		}
	}
}
