function getTask() {
	var check = true;
	var sac_task = new GlideRecord('x_shce_systems_acc_sac_task');
	sac_task.addQuery('active',true); //query for only active records
	sac_task.addQuery('sac_request',current.sys_id);
	sac_task.query();
	if(sac_task.next()){
		check = false; //returns true if the task are not yet closed.
	}
	return check;
}

