sys_variable_value.value:
var ar = new GlideRecord('x_shce_systems_acc_user_system_access');
ar.addQuery('user',current.requested_for);
ar.addNullQuery('deprovision_date');
ar.addNullQuery('deprovision_request');
ar.query();
while (ar.next()) {
	var sr = new GlideRecord("x_shce_systems_acc_sac_task");
	sr.initialize();
	sr.short_description = "Systems Access Request - Remove Access " + ar.application.name + ' ' + ar.application.u_environment;
	sr.assignment_group = ar.application.support_group;
	sr.sac_request = current.sys_id;
	sr.parent = current.sys_id;
	sr.approval = 'approved';
	sr.cmdb_ci = ar.application;
	sr.insert();
}

